import React, { useState, useEffect} from 'react'
import {Switch} from 'antd'

const api = {
  key: "045530b3196e77c7a0aefff762653af0", 
  base: "https://api.openweathermap.org/data/2.5/weather"
}

function App() {

  const[query, setQuery] = useState('')
  const[weather, setWeather] = useState({})
  const[zip, setZip] = useState(10036)

  useEffect(() =>{
    console.log('render');
  }, [])


  const search = event => {
    if(event.key === "Enter"){
      fetch(`${api.base}?zip=${query}&units=imperial&appid=${api.key}`)
        .then(res => res.json())
        .then(result => {
          setZip(result)
          setWeather(result)
          setQuery('')
          console.log(result);
          console.log(result.main);

        });
    }
  }
  setQuery(10001)

  const dateBuilder = (d) => {
    let months = ["January", "February", "March", "April", "May", "June", "July", "August", "September", "October", "November", "December"];
    let days = ["Sunday", "Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday"];

    let day = days[d.getDay()]
    let date = d.getDate()
    let month = months[d.getMonth()]
    let year = d.getFullYear()

    return `${day} ${date} ${month} ${year}`
  }


  return (
    <div className= 'App'>
      <main>
        <div className="search-box">
          <input 
            type="text"
            className="search-bar"
            placeholder="Search..."
            onChange={e => {setQuery(e.target.value)}}
            value={query}
            onKeyDown={search}
          />
        </div>

        {(typeof weather.main !== 'undefined') ? (
        <div>
            <div className="location-box">
              <div className="date">{dateBuilder(new Date())}</div>
              <div className="location">{weather.name}, {weather.sys.country}</div>
            </div>
              <div className="weather-box">
                  <div className="temp">
                    {Math.round(weather.main.temp)}°F <br />
                  </div>
                  <div className="weather">
                    {weather.weather[0].description} <br />
                  </div>

                  High: {Math.round(weather.main.temp_max)}°F <br />
                  Low: {Math.round(weather.main.temp_min)}°F

                <div className="toggles">

                  Wind Speed: <Switch /> {Math.round(weather.wind.speed)} mph <br />
                  humidity: {weather.main.humidity}%  <br />
                  pressure: {weather.main.pressure} <br />
                  sunrise/sunset: {weather.sys.sunrise} {weather.sys.sunset}
                </div>


              </div>
            </div>
        ) : ('')}

      </main>
    </div>
  );
}

export default App;
