import React, { useState } from 'react'

const api = {
  key: "045530b3196e77c7a0aefff762653af0", 
  base: "https://api.openweathermap.org/data/2.5/forecast"
}

function App() {

  const[query, setQuery] = useState('')
  const[weather, setWeather] = useState({})
  const[zip, setZip] = useState(10036)


  const search = event => {
    if(event.key === "Enter"){
      fetch(`${api.base}?zip=${query}&units=imperial&appid=${api.key}`)
        .then(res => res.json())
        .then(result => {
          setZip(result)
          setWeather(result)
          setQuery('')
          console.log(result);
          console.log(result.list[0].main);
          console.log(typeof weather.main);
        });
    }
  }

  const dateBuilder = (d) => {
    let months = ["January", "February", "March", "April", "May", "June", "July", "August", "September", "October", "November", "December"];
    let days = ["Sunday", "Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday"];

    let day = days[d.getDay()]
    let date = d.getDate()
    let month = months[d.getMonth()]
    let year = d.getFullYear()

    return `${day} ${date} ${month} ${year}`
  }


  return (
    <div className= 'App'>
      <main>
        <div className="search-box">
          <input 
            type="text"
            className="search-bar"
            placeholder="Search..."
            onChange={e => {setQuery(e.target.value)}}
            value={query}
            onKeyDown={search}
          />
        </div>

        {(typeof weather.city !== 'undefined') ? (
        <div>
            <div className="location-box">
              <div className="date">{dateBuilder(new Date())}</div>
              <div className="location">{weather.city.name}, {weather.city.country}</div>
            </div>
              <div className="weather-box">
                <div className="weather">
                  {weather.list[0].weather[0].description}

                </div>
              </div>
            </div>
        ) : ('')}

      </main>
    </div>
  );
}

export default App;
